CREATE TABLE KNU_USER(
    Name        VARCHAR(15)     NOT NULL,
    UserId      VARCHAR(15)     NOT NULL,
    Password    VARCHAR(15)     NOT NULL, 
    Department  VARCHAR(15)     NOT NULL,
    StudentId   NUMBER          NOT NULL,
    UNIQUE(StudentId),
    PRIMARY KEY(UserId)
);
    
CREATE TABLE MANAGER(
    ManagerId VARCHAR(15) NOT NULL,
    MPassword VARCHAR(15) NOT NULL,
    PRIMARY KEY (ManagerId)
);

CREATE TABLE ROOM (
    RoomNumber      NUMBER  NOT NULL,
    Classification  VARCHAR(15),
    MaxAvailable    NUMBER  DEFAULT 4 NOT NULL,
    PRIMARY KEY (RoomNumber)
);

CREATE TABLE TIMELINE (
    TimelineRno     NUMBER NOT NULL,
    TimelineId  NUMBER NOT NULL,
    StartTime   NUMBER NOT NULL,
    EndTime     NUMBER NOT NULL,
    PRIMARY KEY (TimelineRno, TimelineId),
    FOREIGN KEY (TimelineRno) REFERENCES ROOM(RoomNumber) ON DELETE CASCADE
);

CREATE TABLE NOTICE(
    NoticeMid   VARCHAR(15)     NOT NULL,
    NoticeId    NUMBER          NOT NULL,
    NTitle      VARCHAR(50)     NOT NULL,
    NContent    VARCHAR(300)    NOT NULL,
    WrittenTime DATE            NOT NULL,
    PRIMARY KEY (NoticeId, NoticeMid),
    FOREIGN KEY (NoticeMid) REFERENCES MANAGER(ManagerId) ON DELETE CASCADE
);

CREATE TABLE REPORT(
    ReportId    NUMBER          NOT NULL,
    ReportUid   VARCHAR(15)     NOT NULL,
    RTitle      VARCHAR(50)     NOT NULL,
    RContent    VARCHAR(300)    NOT NULL,
    RTime       DATE            NOT NULL,
    ReportMid   VARCHAR(15),
    RAnswer     VARCHAR(300), 
    RAnsTime    DATE,
    PRIMARY KEY (ReportId, ReportUid),
    FOREIGN KEY (ReportUid) REFERENCES KNU_USER(UserId) ON DELETE CASCADE,
    FOREIGN KEY (ReportMid) REFERENCES MANAGER(ManagerId) ON DELETE CASCADE
);

CREATE TABLE RATING (
    RatingId    NUMBER          NOT NULL,
    RateUid     VARCHAR(15)     NOT NULL, -- User Id
    RateRno     NUMBER          NOT NULL, -- Room Number
    Rate        NUMBER          NOT NULL,
    RComment     VARCHAR(300)    NOT NULL,
    PRIMARY KEY(RatingId, RateUid, RateRno),
    FOREIGN KEY(RateUid) REFERENCES KNU_USER(UserId) ON DELETE CASCADE,
    FOREIGN KEY(RateRno) REFERENCES ROOM(RoomNumber) ON DELETE CASCADE
);

CREATE TABLE RESERVES (
    ReserveUid  VARCHAR(15) NOT NULL,
    ReserveRno  NUMBER NOT NULL,
    ReserveTid  NUMBER NOT NULL,
    Rdate       DATE NOT NULL,
    PRIMARY KEY (ReserveUid, ReserveRno, ReserveTid, Rdate),
    FOREIGN KEY (ReserveUid) REFERENCES KNU_USER(UserId) ON DELETE CASCADE,
    FOREIGN KEY (ReserveRno, ReserveTid) REFERENCES Timeline(TimelineRno, TimelineId) ON DELETE CASCADE
);

